// Get all input messages
const messages = $input.all().map(item => item.json);

// Extract all 'text' values and filter out nulls
const combinedText = messages
  .map(message => message.text)
  .filter(text => text !== null && text !== undefined)
  .join('\n');

// Return the result
return [
  {
    json: {
      combinedText,
    },
  },
];

